import React, { useState } from "react";
import logo from "./logo.svg";
import "./App.css";

function loadScript(src) {
  return new Promise((resolve) => {
    const script = document.createElement("script");
    script.src = src;
    script.onload = () => {
      resolve(true);
    };
    script.onerror = () => {
      resolve(false);
    };
    document.body.appendChild(script);
  });
}

const __DEV__ = document.domain === "localhost";

function App() {
  const [name, setName] = useState("Mehul");

  async function displayRazorpay() {
    const res = await loadScript(
      "https://checkout.razorpay.com/v1/checkout.js"
    );

    if (!res) {
      alert("Razorpay SDK failed to load. Are you online?");
      return;
    }

    // todo : change the endpoint with your api of the create payment order
    const endpoint = `http://localhost:3001/api/v1/bookings/book/event`;

    // todo : change the payload based on your api
    const payload = {
      EventId: 1,
      entries: 5,
      createTicketForSelf: true,
    };

    // todo : change the bearerToken to a valid jwt token of your project
    const bearerToken =
      "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..102SJFIbxgYuLiRX3psEEjTVd7RNvCJb3wuMzW6Q3vs";

    const order = await fetch(endpoint, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorizaton: "Bearer " + bearerToken,
      },
      body: JSON.stringify(payload),
    }).then((t) => t.json());

    console.log("Payment order created --->", order);

    return;

    const options = {
      // key: __DEV__ ? 'rzp_test_uGoq5ABJztRAhk' : 'PRODUCTION_KEY',
      key: "rzp_test_gJNpaDGvyUrYzN",
      currency: order.currency,
      amount: order.amount.toString(),
      order_id: order.id,
      name: "Donation",
      description: "Thank you for nothing. Please give us some money",
      // image: "http://localhost:1337/logo.svg",
      notes: {
        address: "Razorpay Corporate Office",
      },
      handler: function (response) {
        console.log("res from handler\n", response);
        alert(response.razorpay_payment_id);
        alert(response.razorpay_order_id);
        alert(response.razorpay_signature);
      },
      prefill: {
        name: "Jay Jethava",
        email: "jayjethva2018@gmail.com",
        phone_number: "9924170840",
      },
    };
    const paymentObject = new window.Razorpay(options);
    paymentObject.open();
  }

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          onClick={displayRazorpay}
          target="_blank"
          rel="noopener noreferrer"
        >
          Donate $5
        </a>
      </header>
    </div>
  );
}

export default App;
